(25, 'default', NULL, 1, 'published', 'mikumi-national-park', '/tanzania/mikumi-national-park', NULL, 'destinations', '{\"title\":\"Mikumi National Park\",\"description\":null,\"featured_image\":\"tanzania\\/mikumi-national-park.jpg\",\"updated_by\":\"a0ef8da5-9dc9-4e55-9b48-0fe197a56747\",\"blueprint\":\"park\",\"seotamic_meta\":null,\"seotamic_title\":\"title\",\"seotamic_custom_title\":null,\"seotamic_title_prepend\":true,\"seotamic_title_append\":true,\"seotamic_meta_description\":\"empty\",\"seotamic_custom_meta_description\":null,\"seotamic_canonical\":null,\"seotamic_social\":null,\"seotamic_open_graph_title\":\"title\",\"seotamic_custom_open_graph_title\":null,\"seotamic_open_graph_description\":\"general\",\"seotamic_custom_open_graph_description\":null,\"seotamic_twitter_title\":\"title\",\"seotamic_custom_twitter_title\":null,\"seotamic_twitter_description\":\"general\",\"seotamic_custom_twitter_description\":null,\"seotamic_image\":null,\"blocks\":[{\"style\":\"style_1\",\"background_color\":\"#ffffff\",\"title\":\"About Mikumi National Park\",\"description\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"The landscapes in Mikumi National Park can easily be compared to those of the Serengeti National Park as acacias, baobabs and tamarinds dot the savannah. Mikumi National Park is the perfect safari location when you don\\u2019t have the time for an extensive safari.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"The landscape of Mikumi National Park is often compared to that of the \\\"SERENGETI\\\" and for that reason is called \\u2018Little Serengeti\\u2019.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"The road that crosses the park divides the park into two areas with partially different environments. The northwest consists of savannah scattered with acacias, baobab trees, tamarinds and a rare palm. In this area, furthest from the road, there are spectacular rock formations of the mountains Rubeho and Uluguru. The southeastern part of the park has fewer wildlife and is not very accessible.\"}]}],\"has_entries\":false,\"type\":\"content\",\"enabled\":true}]}', '2022-09-15 16:50:24', '2022-11-03 17:44:15'),
(26, 'default', NULL, 1, 'published', 'ruaha-national-park', '/tanzania/ruaha-national-park', NULL, 'destinations', '{\"title\":\"Ruaha National Park\",\"description\":\"The vegetation in Ruaha National Park differs drastically between flat treeless savannahs, Miombo woodlands, dry bush lands, swamps and riverine forests. Almost 1650 different plant species can be found in the park, creating a one of a kind botanical paradise.\",\"featured_image\":\"ruaha\\/ruaha-2.jpeg\",\"updated_by\":\"14d2aaab-790f-448d-b89c-6789b02abf47\",\"blueprint\":\"park\"}', '2022-09-15 16:51:04', '2022-09-19 23:51:07'),
(27, 'default', NULL, 1, 'published', 'saadani-national-park', '/tanzania/saadani-national-park', NULL, 'destinations', '{\"title\":\"Saadani National Park\",\"description\":null,\"featured_image\":\"tanzania\\/saadani-national-park-1.webp\",\"updated_by\":\"a0ef8da5-9dc9-4e55-9b48-0fe197a56747\",\"blueprint\":\"park\",\"blocks\":[{\"style\":\"style_1\",\"background_color\":\"#ffffff\",\"title\":\"About Saadani National Park\",\"description\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"The park is situated between Dar es Salaam (200 km, 4 hours) and Tanga (75km, 3 hours) and borders the mainland coast. Saadani National Park is one of the newest national parks in Tanzania and relatively small compared to other parks. \"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Saadani National Park is located attractively on the shores of the Indian Ocean. Elephants and other wild animals roam the beaches, which are among the few remaining breeding grounds for green turtles on mainland Tanzania. The Wami River flowing through the park is flanked by mangrove trees and is the habitat of hippos, crocodiles and a high concentration of river birds.\"}]}],\"has_entries\":false,\"type\":\"content\",\"enabled\":true}]}', '2022-09-15 16:52:00', '2022-11-03 17:47:44'),
(28, 'default', NULL, 1, 'published', 'nyerere-national-park', '/tanzania/nyerere-national-park', NULL, 'destinations', '{\"title\":\"Nyerere National Park\",\"description\":null,\"featured_image\":\"tanzania\\/nyerere-national-park.jpg\",\"updated_by\":\"a0ef8da5-9dc9-4e55-9b48-0fe197a56747\",\"blueprint\":\"park\",\"seotamic_meta\":null,\"seotamic_title\":\"title\",\"seotamic_custom_title\":null,\"seotamic_title_prepend\":true,\"seotamic_title_append\":true,\"seotamic_meta_description\":\"empty\",\"seotamic_custom_meta_description\":null,\"seotamic_canonical\":null,\"seotamic_social\":null,\"seotamic_open_graph_title\":\"title\",\"seotamic_custom_open_graph_title\":null,\"seotamic_open_graph_description\":\"general\",\"seotamic_custom_open_graph_description\":null,\"seotamic_twitter_title\":\"title\",\"seotamic_custom_twitter_title\":null,\"seotamic_twitter_description\":\"general\",\"seotamic_custom_twitter_description\":null,\"seotamic_image\":null,\"blocks\":[{\"style\":\"style_1\",\"title\":\"About Nyerere National Park\",\"description\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Nyerere National Par is part of the largest game reserve in Africa, with large populations of elephants and buffalos. From the characteristic Rufiji river, you can see the natural behavior of the animals up close.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Nyerere National Park has grassland, typical Acacia savannah, lakes and rivers and extensive Miombo forests.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"The number of animals in the Nyerere National Park is huge. Nowhere else can the elephant, buffalo and lion be found in greater numbers throughout the year. Besides, Nyerere National Park is also a famous habitat for the last African wild dogs.\"}]}],\"has_entries\":false,\"type\":\"content\",\"enabled\":true}]}', '2022-09-15 16:56:39', '2022-11-03 17:40:25'),
