(44, 'default', NULL, 1, 'published', 'rwanda', '/rwanda', NULL, 'destinations', '{\"title\":\"Rwanda\",\"blocks\":[{\"style\":\"style_1\",\"title\":\"Rwanda Holidays\",\"description\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Rwanda is home to the mountain gorillas in Africa. Visit Volcanoes National Park for the Rwanda gorilla trekking safaris. Rwanda Safari Holidays\\u00a0allow you to discover a unique part of Africa, experiencing the best Gorilla trekking an abundance of wildlife & history.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Rwanda Safari holidays\\u00a0consist of gorilla tracking tour, primates to three country three national parks the Volcanoes , Akagera and Nyungwe National Parks.\"}]},{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"marks\":[{\"type\":\"bold\"}],\"text\":\"Why You Should Visit Rwanda\"}]},{\"type\":\"bullet_list\",\"content\":[{\"type\":\"list_item\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"The Mountain Gorillas Trekking\"}]}]},{\"type\":\"list_item\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Visit Wildlife in Their Natural Habitat\"}]}]},{\"type\":\"list_item\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"Its Safety and Cleanliness\"}]}]},{\"type\":\"list_item\",\"content\":[{\"type\":\"paragraph\",\"content\":[{\"type\":\"text\",\"text\":\"The Cultural People Of Rwanda\"}]}]}]}],\"has_entries\":false,\"type\":\"content\",\"enabled\":true,\"background_color\":\"#ffffff\"},{\"style\":\"style_2\",\"title\":\"Top Rwanda National Parks\",\"has_entries\":true,\"grid_columns\":4,\"popular_entries\":[70,69,68,190],\"type\":\"popular\",\"enabled\":true,\"has_cards\":false,\"background_color\":\"#f5f5f5\"},{\"style\":\"style_1\",\"title\":\"Rwanda Gorilla Trekking & Safari Tours\",\"has_entries\":true,\"grid_entries\":[86,87,88],\"toggle_button\":false,\"type\":\"packages\",\"enabled\":true,\"background_color\":\"#ffffff\",\"entries_list\":[191,192,193],\"toggle_tabs\":false}],\"featured_image\":\"rwanda\\/rwanda-baby-gorilla-virunga.jpg\",\"updated_by\":\"7dba8970-3db2-4822-9aba-29c5bd926255\",\"blueprint\":\"country\",\"section_basic\":null,\"seo_title\":null,\"seo_description\":null,\"section_advanced\":null,\"seo_noindex\":false,\"seo_nofollow\":false,\"seo_canonical_type\":null,\"seo_canonical_current\":null,\"seo_canonical_external\":null,\"section_og\":null,\"og_title\":null,\"og_description\":null,\"og_image\":null,\"section_sitemap\":null,\"sitemap_change_frequency\":\"weekly\",\"sitemap_priority\":\"0.5\"}', '2022-09-19 18:08:09', '2023-09-04 14:23:35');
