(215, 'default', NULL, 1, 'published', 'cultural-tours', '/cultural-tours', NULL, 'pages', '{\"title\":\"Cultural Tours\",\"blocks\":[{\"style\":\"style_1\",\"title\":\"Tanzania Cultural Tours\",\"tabs\":[{\"tab_heading\":\"Maasai\",\"is_active\":true,\"image\":\"tanzania\\/ma5.jpg\",\"title\":\"Maasai Village Visit\",\"has_entries\":false,\"toggle_button\":false,\"type\":\"tab\",\"enabled\":true,\"description\":\"Visiting a Maasai village is one of the cultural highlights of our traveller\'s dream activities in Tanzania. Visit the Maasai - one of East Africa\'s most known and fascinaing tribes!\"},{\"tab_heading\":\"Handzabe\",\"is_active\":false,\"image\":\"tanzania\\/hadzabe-tribe-arusha-tanzania-.jpg\",\"title\":\"Handzabe Tribe Visit\",\"has_entries\":false,\"toggle_button\":false,\"type\":\"tab\",\"enabled\":true,\"description\":\"The Hadzabe bushmen live in and around Lake Eyasi, just south of the Ngorongoro Highlands. Experience full-service, guided adventure travel tours with any of our pre-planned, custom, or private Hadzabe trips and vacations.\"},{\"tab_heading\":\"Datoga\",\"is_active\":false,\"image\":\"tanzania\\/c0436503-800px-wm.jpg\",\"title\":\"Datoga Tribe Visit\",\"has_entries\":false,\"toggle_button\":false,\"type\":\"tab\",\"enabled\":true,\"description\":\"The Datoga tribe in Tanzania are a semi nomadic and pastoralist tribe of Tanzania who live in the northern part of Tanzania around the Manyara region.\"}],\"type\":\"tabs\",\"enabled\":true}],\"author\":\"f497ed6c-79db-4d58-a54f-e2d831372f2d\",\"parent\":null,\"template\":null,\"featured_image\":\"tanzania\\/hadzabe-tanzania.jpeg\",\"section_basic\":null,\"seo_title\":null,\"seo_description\":null,\"section_advanced\":null,\"seo_noindex\":false,\"seo_nofollow\":false,\"seo_canonical_type\":null,\"seo_canonical_current\":null,\"seo_canonical_external\":null,\"section_og\":null,\"og_title\":null,\"og_description\":null,\"og_image\":null,\"section_sitemap\":null,\"sitemap_change_frequency\":\"